import React from 'react'
import { useMemo, useState } from 'react'
import { makeRenderer, useTable } from 'react-table'
import 'bootstrap/dist/css/bootstrap.min.css'
import Button from 'react-bootstrap/Button'
import { Popup } from './Popup'

export const AppletTable = () => {

  const columns = useMemo(() => [
    {
      Header: 'ID',
      accessor: 'id'
    },
    {
      Header: 'Name',
      accessor: 'name'
    },
    {
      Header: 'E-mail',
      accessor: 'email'
    },
    {
      Header: 'Password',
      accessor: 'password'
    },
    {
      Header: 'Admin',
      accessor: 'admin'
    }
  ], []);

  const [users, setUsers] = useState([]);
  const [popupState, setPopupState] = useState(false);
  const selectedUsers = {};

  const showAddForm = event => {
    console.log("Okay");
    setPopupState(!popupState);
  };

  if(popupState) {
    return (
      <Popup ConfirmAddUser = {
        newUser => {
          setPopupState(!popupState);
          setUsers([...users, newUser]);
        }
      } cancelAddUser = {
          () => {
            setPopupState(!popupState);
          }
      }/>
    )
  }

  return (
    <div>
      <div className='table-container'>
        <table className='table table-hover'>
          <thead className='table-light'>
            <tr>
              <th>#</th>
              <th>ID</th>
              <th>Name</th>
              <th>E-mail</th>
              {/* <th>Password</th> */}
              <th>Admin</th>
            </tr>
          </thead>
          <tbody>
            {
              users.map(user => {
                return (
                  <tr>
                    <td>
                      <input type="checkbox" value='Yes' onChange={() => 
                        {
                          if(user.id in selectedUsers)
                            delete selectedUsers[user.id];
                          else
                            selectedUsers[user.id] = user;
                        }
                      }/>
                    </td>
                    <td>{user.id}</td>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    {/* <td>{user.password}</td> */}
                    <td>{user.admin}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
      <div className='btns-container'>
        <div className='btns'>
          <Button variant="outline-primary" onClick={showAddForm}> add user </Button>{' '}
          <Button variant="outline-success"> update user </Button>{' '}
          <Button variant="outline-danger"> remove user </Button>
        </div>
      </div>
    </div>
  )
}
