import React from 'react'
import { useMemo, useState } from 'react'
import { makeRenderer, useTable } from 'react-table'
import 'bootstrap/dist/css/bootstrap.min.css'
import Button from 'react-bootstrap/Button'
import { Popup } from './Popup'

export const AppletTable = () => {

  const columns = useMemo(() => [
    {
      Header: 'Name',
      accessor: 'name'
    },
    {
      Header: 'E-mail',
      accessor: 'email'
    },
    {
      Header: 'Admin',
      accessor: 'admin'
    }
  ], []);

  const [users, setUsers] = useState([]);
  const [popupState, setPopupState] = useState(false);

  const showAddForm = event => {
    console.log("Okay");
    setPopupState(!popupState);
  };

  if(popupState) {
    return (
      <Popup ConfirmAddUser = {
        newUser => {
          setPopupState(!popupState)
          setUsers([...users, newUser])
        }
      }/>
    )
  }

  return (
    <div>
      <div className='table-container'>
        <table className='table'>
          <thead className='table-light'>
            <tr>
              <th>Name</th>
              <th>E-mail</th>
              <th>isAdmin</th>
            </tr>
          </thead>
          <tbody>
            {
              users.map(user => {
                return (
                  <tr>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.admin}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
      <div className='btns-container'>
        <div className='btns'>
          <Button variant="outline-primary" onClick={showAddForm}> add user </Button>{' '}
          <Button variant="outline-success"> update user </Button>{' '}
          <Button variant="outline-danger"> remove user </Button>
        </div>
      </div>
    </div>
  )
}
