import React from 'react'
import { useMemo, useState } from 'react'
import { makeRenderer, useTable } from 'react-table'
import 'bootstrap/dist/css/bootstrap.min.css'
import Button from 'react-bootstrap/Button'
import { Popup } from './Popup'
import { PopupUpdate } from './PopupUpdate'

export const AppletTable = () => {

  const columns = useMemo(() => [
    {
      Header: 'ID',
      accessor: 'id'
    },
    {
      Header: 'Name',
      accessor: 'name'
    },
    {
      Header: 'E-mail',
      accessor: 'email'
    },
    {
      Header: 'Password',
      accessor: 'password'
    },
    {
      Header: 'Admin',
      accessor: 'admin'
    }
  ], []);

  const [users, setUsers] = useState([]);
  const [popupState, setPopupState] = useState(false);
  const [updateState, setUpdateState] = useState(false);
  const selectedUsers = {};

  const showAddForm = () => {
    setPopupState(!popupState);
  };

  const showUpdateForm = () => {
    setPopupState(!popupState);
    setUpdateState(!updateState);
  };

  if(popupState) {
    if(updateState) {
      if(Object.keys(selectedUsers).length != 1) {
        alert('Number of users must be exactly one')
        setPopupState(!popupState);
        setUpdateState(!updateState);
        selectedUsers = {};
      } else {
        for(let userId in selectedUsers) {
          return (
            <PopupUpdate ConfirmAddUser = {
              updatedUser => {
                selectedUsers.userId.name = updatedUser.name;
                selectedUsers.userId.email = updatedUser.email;
                selectedUsers.userId.password = updatedUser.password;
                selectedUsers.userId.admin = updatedUser.admin;
                selectedUsers = {};
              }
            } cancelAddUser = {
                () => {
                  setPopupState(!popupState);
                  setUpdateState(!updateState);
                  selectedUsers = {};
                }
            } userId={userId}/>
          )
        }
      }
    }
    return (
      <Popup ConfirmAddUser = {
        newUser => {
          setPopupState(!popupState);
          setUsers([...users, newUser]);
        }
      } cancelAddUser = {
          () => {
            setPopupState(!popupState);
          }
      }/>
    )
  }

  return (
    <div>
      <div className='table-container'>
        <table className='table table-hover'>
          <thead className='table-light'>
            <tr>
              <th>#</th>
              {/* <th>ID</th> */}
              <th>Name</th>
              <th>E-mail</th>
              <th>Password</th>
              <th>Admin</th>
            </tr>
          </thead>
          <tbody>
            {
              users.map(user => {
                return (
                  <tr>
                    <td>
                      <input type="checkbox" value='Yes' onChange={() => 
                        {
                          if(user.id in selectedUsers)
                            delete selectedUsers[user.id];
                          else
                            selectedUsers[user.id] = user;
                        }
                      }/>
                    </td>
                    {/* <td>{user.id}</td> */}
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.password}</td>
                    <td>{user.admin}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
      <div className='btns-container'>
        <div className='btns'>
          <Button variant="outline-primary" onClick={showAddForm}> add user </Button>{' '}
          (Object.keys(selectedUsers).length != 1 ?  
            <Button variant="outline-success" disabled> update user </Button>{' '} :
            <Button variant="outline-success" onClick={showUpdateForm}> update user </Button>{' '}
          )
          <Button variant="outline-danger"> remove user </Button>
        </div>
      </div>
    </div>
  )
}
