import { useState, useEffect, useRef } from 'react';
import { LoginForm }  from './components/LoginForm';
import { AppletTable } from './components/AppletTable';
import { AppletNavbar } from './components/AppletNavbar';
import './App.css'

function App() {
  const [user, setUser] = useState({ email: '', password: '' });
  const [error, serError] = useState('');
  const [loginPressed, setLoginPressed] = useState(false);
  let userPrivilageAdmin = 'No';

  const Login = info => {
    setUser({
      email: info.email,
      password: info.password
    })
    setLoginPressed
  };

  const Logout = () => {
    setUser({
      email: '',
      password: ''
    })
  };

  useEffect(() => {
    const checkUser = async () => {
      if(isInitialMount.current) {
        isInitialMount.current = false;
        return;
      }

      try {
        const res = await fetch(`/users/login/${user.email}/${user.password}`);
        if(!res.ok)
          throw Error('Email or password are wrong');

        const fetchedUser = await res.json();
        userPrivilageAdmin = fetchedUser.admin;
      } catch(err) {
        alert(err);
      }
    }

    checkUser();
  })

  if(userPrivilageAdmin != 'No') {
    return (
      <div className="App">
          <AppletNavbar/>
          <AppletTable/>
      </div>
    );
  }

  return (
    <div className="App"> 
        <LoginForm Login={Login} error={error}/>
    </div>
  );
}

export default App;
