import React from 'react'
import { useState, useEffect } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'

export const AppletTable = ({ privilage }) => {
  const [users, setUsers] = useState([]);
  const selectedUsers = new Set();

  useEffect(() => {
    const fetchUsers = async () => { 
      try {
        const res = await fetch('/users');

        if(!res.ok)
          throw Error('Fetching users causes an error check your connections')

        setUsers(await res.json());
      } catch(err) {
          alert(err);
      } 
    }

    fetchUsers();
  }, []);

  return (
    <div className='table-container'>
      <table className='table table-hover'>
        <thead className='table-light'>
          <tr>
            {
              privilage === 'Yes' ? <th>#</th> : ''
            }
            <th>ID</th>
            <th>Name</th>
            <th>E-mail</th>
            <th>Admin</th>
          </tr>
        </thead>
        <tbody>
          {
            users.map((user, index) => {
              return (
                <tr key={user._id}>
                  {
                    privilage === 'Yes' ?
                    (
                      <td>
                        <input type="checkbox" value='Yes' onChange={
                          () => {
                            if(selectedUsers.has(user._id))
                              selectedUsers.delete(user._id);
                            else
                              selectedUsers.add(user._id);
                            console.log(selectedUsers.size);
                            for(const id of selectedUsers)
                              console.log(id);
                          }
                        }/>
                      </td>
                    ) : ''
                  }
                  <td>{user._id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.admin}</td>
                </tr>
              )
            })
          }
        </tbody>
      </table>
    </div>
  )
}
