import { useState, useEffect, useRef } from 'react';
import { LoginForm }  from './components/LoginForm';
import { AppletTable } from './components/AppletTable';
import { AppletNavbar } from './components/AppletNavbar';
import './App.css'

function App() {
  const [userPrivilage, setUserPrivilagge] = useState({ autherized: false, admin: 'No' });
  const [user, setUser] = useState({ email: '', password: '' });
  const [error, setError] = useState('');

  const Login = info => {
    console.log("from login");
    setUser({
      email: info.email,
      password: info.password
    })
  };

  const Logout = () => {
    setUser({
      email: '',
      password: ''
    })
  };

  useEffect(() => {
    const checkUser = async () => {
      if(user.email == '' || user.password == '') return;

      try {
        const res = await fetch(`/users/login/${user.email}/${user.password}`);
        if(!res.ok)
          throw Error('Email or password are wrong');

        const fetchedUser = await res.json();
        setUserPrivilagge({
          autherized: true,
          admin: fetchedUser.admin
        })
      } catch(err) {
        alert(err);
      }
    }

    checkUser();
  }, [user])

  if(userPrivilage.autherized) {
    return (
      <div className="App">
          <AppletNavbar/>
          <AppletTable/>
      </div>
    );
  }

  return (
    <div className="App"> 
        <LoginForm Login={Login} error={error}/>
    </div>
  );
}

export default App;
