import { useState, useEffect } from 'react';
import { LoginForm }  from './components/LoginForm';
import { AppletTable } from './components/AppletTable';
import { AppletNavbar } from './components/AppletNavbar';
import { UserManipBtns } from './components/UserManipBtns'

import './App.css'

function App() {
  const [userPrivilage, setUserPrivilagge] = useState({ autherized: false, admin: 'No' });
  const [user, setUser] = useState({ email: '', password: '' });

  const Login = info => {
    setUser({
      email: info.email,
      password: info.password
    })
  };

  const Logout = () => {
    setUser({
      email: '',
      password: ''
    });

    setUserPrivilagge({
      autherized: false,
      admin: 'No'
    });
  };

  useEffect(() => {
    const checkUser = async () => {
      if(user.email === '' || user.password === '') return;

      try {
        const res = await fetch(`/users/login/${user.email}/${user.password}`);
        if(!res.ok)
          throw Error('Email or password are wrong');

        const fetchedUser = await res.json();
        if(fetchedUser.length === 0)
          throw Error('Email or password are wrong');

        console.log(fetchedUser);
        setUserPrivilagge({
          autherized: true,
          admin: fetchedUser[0].admin
        })
        console.log(userPrivilage.admin === 'Yes');
      } catch(err) {
        alert(err);
      }
    }

    checkUser();
  }, [user])

  if(userPrivilage.autherized) {
    return (
      <div className="App">
          <AppletNavbar logoutHandler={Logout}/>
          <AppletTable privilage={ userPrivilage.admin}/>
          {
          userPrivilage.admin === 'Yes' ? <UserManipBtns/> : ''
          }
      </div>
    );
  }

  return (
    <div className="App"> 
        <LoginForm Login={Login}/>
    </div>
  );
}

export default App;
