import React, { useState } from 'react'
import { Button } from 'react-bootstrap';

export const Popup = ({ ConfirmAddUser, cancelAddUser }) => {
  const [newUser, setNewUser] = useState({id: Math.random, name: '', email: '', password: '', admin:'No'});

  const submitHandler = event => {
    event.preventDefault();

    ConfirmAddUser(newUser);
  }

  const cancelHandler = event => {
    event.preventDefault();

    cancelAddUser();
  }

  return (
    <div className="Auth-form-container popup-box">
      <form className="Auth-form" onSubmit={submitHandler}>
        <div className="Auth-form-content">
          <h3 className="Auth-form-title">New User</h3>
          <div className="form-group mt-3">
            <label>Name</label>
            <input
              type="text"
              required
              className="form-control mt-1"
              placeholder="Enter Name"
              onChange={event => setNewUser({...newUser, name: event.target.value})} 
              value={newUser.name}
            />
          </div>
          <div className="form-group mt-3">
            <label>Email</label>
            <input
              type="email"
              required
              className="form-control mt-1"
              placeholder="Enter email"
              onChange={event => setNewUser({...newUser, email: event.target.value})} 
              value={newUser.email}
            />
          </div>
          <div className="form-group mt-3">
            <label>Password</label>
            <input
              type="password"
              required
              className="form-control mt-1"
              placeholder="Enter password"
              onChange={event => setNewUser({...newUser, password: event.target.value})} 
              value={newUser.password}
            />
          </div>
          <div className="mt-3">
            <label>
              <input
                type="checkbox"
                value='Yes'
                onChange={() => setNewUser({...newUser, admin: newUser.admin == 'Yes' ? 'No' : 'Yes'})}
              />
              <span> Admin </span>
            </label>
          </div>
          <div className="d-grid gap-2 mt-3">
            <Button type="submit" variant="outline-primary">Confirm</Button>
            <Button type="submit" variant="outline-danger" onClick={cancelHandler}>Canel</Button>
          </div>
        </div>
      </form>
    </div>
  )
}
