import React from 'react'
import { useState, useEffect } from 'react'
import Button from 'react-bootstrap/Button'

export const UserManipBtns = () => {
  const [newUserData, setNewUserData] = useState({ name: '', email: '', password: '', admin: '' });

  useEffect(() => {
    const addUser = async () => {
      if(newUserData.name === '' || newUserData.email === '' || newUserData.password === '' || newUserData.admin === '') return;

      try {
        const res = await fetch(`/users/add`, {
          method: 'PUT',
          headers: {
            'Content-type': 'application/json',
          },
          body: JSON.stringify({ name: newUserData.name, email: newUserData.email, password: newUserData.password, admin: newUserData.admin })
        });
        if(!res.ok)
          throw Error('Check your inputs\' format');
      } catch(err) {
        alert(err);
      }
    }
  }, [newUserData]);

  return (
    <div className='btns-container'>
      <div className='btns'>
        <Button variant="outline-primary"> add user </Button>{' '}
        <Button variant="outline-success"> update user </Button>{' '}
        <Button variant="outline-danger"> remove user </Button>
      </div>
    </div>
  )
}
