import React from 'react'
import { useMemo, useState } from 'react'
import { makeRenderer, useRowState, useTable } from 'react-table'
import 'bootstrap/dist/css/bootstrap.min.css'
import Button from 'react-bootstrap/Button'
import { Popup } from './Popup'
import { PopupUpdate } from './PopupUpdate'

export const AppletTable = () => {

  const columns = useMemo(() => [
    {
      Header: 'ID',
      accessor: '_id'
    },
    {
      Header: 'Name',
      accessor: 'name'
    },
    {
      Header: 'E-mail',
      accessor: 'email'
    },
    {
      Header: 'Password',
      accessor: 'password'
    },
    {
      Header: 'Admin',
      accessor: 'admin'
    }
  ], []);

  const [users, setUsers] = useState([]);
  const [popupState, setPopupState] = useState(false);
  const [updateState, setUpdateState] = useState(false);
  // const [updateButtonState, setUpdateButtonState] = useState(true);
  // const [deleteButtonState, setDeleteButtonState] = useState(true);
  // const selectedUsers = [];

  const showAddForm = () => {
    setPopupState(!popupState);
  };

  // const showUpdateForm = () => {
  //   setPopupState(!popupState);
  //   setUpdateState(!updateState);
  // };

  if(popupState) {
    if(updateState) {
      return (
        <PopupUpdate ConfirmAddUser = {
          updatedUser => {
            console.log(updatedUser);
            console.log(selectedUsers[0]);
            // selectedUsers[0].name = updatedUser.name;
            // selectedUsers[0].email = updatedUser.email;
            // selectedUsers[0].password = updatedUser.password;
            // selectedUsers[0].admin = updatedUser.admin;
            setPopupState(!popupState);
            setUpdateState(!updateState);
          }
        } cancelAddUser = {
            () => {
              setPopupState(!popupState);
              setUpdateState(!updateState);
            }
        }/>
      )
    }
    return (
      <Popup ConfirmAddUser = {
        newUser => {
          setPopupState(!popupState);
          setUsers([...users, newUser]);
        }
      } cancelAddUser = {
          () => {
            setPopupState(!popupState);
          }
      }/>
    )
  }

  return (
    <div>
      <div className='table-container'>
        <table className='table table-hover'>
          <thead className='table-light'>
            <tr>
              <th>#</th>
              <th>ID</th>
              <th>Name</th>
              <th>E-mail</th>
              <th>Password</th>
              <th>Admin</th>
            </tr>
          </thead>
          <tbody>
            {
              users.map(user => {
                return (
                  <tr>
                    <td>
                      <input type="checkbox" value='Yes' onChange={() => 
                        {
                          // if(user.id in selectedUsers)
                          //   selectedUsers[user.id];
                          // else
                          //   selectedUsers.push(user);
                          // if(selectedUsers.length > 0) {
                          //   setDeleteButtonState(!deleteButtonState);
                          //   if(selectedUsers.length == 1)
                          //     setUpdateButtonState(!updateButtonState);
                          // }
                        }
                      }/>
                    </td>
                    <td>{user._id}</td>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.password}</td>
                    <td>{user.admin}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
      <div className='btns-container'>
        <div className='btns'>
          <Button variant="outline-primary" onClick={showAddForm}> add user </Button>{' '}
          <Button variant="outline-success"> update user </Button>{' '}
          <Button variant="outline-danger"> remove user </Button>
          {/* <Button variant="outline-success" disabled={updateButtonState} onClick={showUpdateForm}> update user </Button>{' '}
          <Button variant="outline-danger" disabled={deleteButtonState}> remove user </Button> */}
        </div>
      </div>
    </div>
  )
}
